query CityFairListQuery(
  $count: Int!
  $cursor: String
  $citySlug: String!
) {
  city(slug: $citySlug) {
    ...CityFairList_city_1G22uz
  }
}

fragment CityFairList_city_1G22uz on City {
  fairs(first: $count, after: $cursor, status: CURRENT, sort: START_AT_ASC) {
    edges {
      node {
        id
        name
        exhibition_period
        counts {
          partners
        }
        location {
          coordinates {
            lat
            lng
          }
          __id
        }
        image {
          image_url
          aspect_ratio
          url
        }
        profile {
          icon {
            id
            href
            height
            width
            url(version: "square140")
          }
          __id
          id
          name
        }
        start_at
        end_at
        __id
        __typename
      }
      cursor
    }
    pageInfo {
      endCursor
      hasNextPage
    }
  }
}
