query FairArtistsQuery(
  $id: String!
  $count: Int!
  $cursor: String
) {
  fair(id: $id) {
    ...FairArtists_fair_1G22uz
    __id
  }
}

fragment FairArtists_fair_1G22uz on Fair {
  id
  artists(first: $count, after: $cursor) {
    pageInfo {
      hasNextPage
      startCursor
      endCursor
    }
    edges {
      cursor
      node {
        ...ArtistListItem_artist
        sortable_id
        __id
        __typename
      }
    }
  }
  __id
}

fragment ArtistListItem_artist on Artist {
  id
  __id
  name
  is_followed
  nationality
  birthday
  deathday
  image {
    url
  }
}
