query FairHeaderTestsQuery {
  fair(id: "sofa-chicago-2018") {
    ...FairHeader_fair
    __id
  }
}

fragment FairHeader_fair on Fair {
  id
  name
  image {
    image_url
    aspect_ratio
    url
  }
  organizer {
    profile {
      icon {
        id
        href
        height
        width
        url
      }
      name
      __id
    }
  }
  start_at
  end_at
  __id
}
