query FairHeaderTestsQuery {
  fair(id: "sofa-chicago-2018") {
    ...FairHeader_fair
    __id
  }
}

fragment FairHeader_fair on Fair {
  id
  name
  exhibitors_grouped_by_name {
    exhibitors
  }
  image {
    image_url
    aspect_ratio
    url
  }
  profile {
    icon {
      id
      href
      height
      width
      url
    }
    name
    __id
  }
  start_at
  end_at
  __id
}
