query FairHeaderTestsQuery {
  fair(id: "sofa-chicago-2018") {
    ...FairHeader_fair
    __id
  }
}

fragment FairHeader_fair on Fair {
  id
  name
  exhibitors_grouped_by_name {
    exhibitors
    profile_ids
  }
  counts {
    artists
    partners
  }
  artists_names: artists(first: 2) {
    edges {
      node {
        name
        href
        __id
      }
    }
  }
  image {
    image_url
    aspect_ratio
    url
  }
  profile {
    icon {
      id
      href
      height
      width
      url
    }
    name
    __id
  }
  start_at
  end_at
  __id
}
