query FairTestsQuery {
  fair(id: "sofa-chicago-2018") {
    ...Fair_fair
    __id
  }
}

fragment Fair_fair on Fair {
  ...FairDetail_fair
  ...FairArtworks_fair
  __id
}

fragment FairDetail_fair on Fair {
  ...FairHeader_fair
  ...ArtworksPreview_fair
  id
  hours
  location {
    ...LocationMap_location
    __id
  }
  organizer {
    profile {
      name
      __id
    }
  }
  __id
}

fragment FairArtworks_fair on Fair {
  __id
  artworks: filteredArtworks(size: 0, medium: "*", price_range: "*-*", aggregations: [MEDIUM, PRICE_RANGE, TOTAL]) {
    ...FilteredInfiniteScrollGrid_filteredArtworks
    __id
  }
}

fragment FilteredInfiniteScrollGrid_filteredArtworks on FilterArtworks {
  ...Filters_filteredArtworks
  ...ArtworksGridPaginationContainer_filteredArtworks
  __id
}

fragment Filters_filteredArtworks on FilterArtworks {
  aggregations {
    slice
    counts {
      id
      name
      __id
    }
  }
  __id
}

fragment ArtworksGridPaginationContainer_filteredArtworks on FilterArtworks {
  __id
  artworks: artworks_connection(first: 10) {
    pageInfo {
      hasNextPage
      startCursor
      endCursor
    }
    edges {
      node {
        id
        __id
        image {
          aspect_ratio
        }
        ...Artwork_artwork
        __typename
      }
      cursor
    }
  }
}

fragment Artwork_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  is_biddable
  is_acquireable
  id
  sale {
    is_auction
    is_live_open
    is_open
    is_closed
    display_timely_at
    __id
  }
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}

fragment FairHeader_fair on Fair {
  id
  name
  image {
    image_url
    aspect_ratio
    url
  }
  organizer {
    profile {
      icon {
        id
        href
        height
        width
        url
      }
      name
      __id
    }
  }
  start_at
  end_at
  __id
}

fragment ArtworksPreview_fair on Fair {
  __id
  filteredArtworks(size: 0, aggregations: [TOTAL]) {
    artworks_connection(first: 6) {
      edges {
        node {
          ...GenericGrid_artworks
          __id
        }
      }
    }
    counts {
      total
    }
    __id
  }
}

fragment LocationMap_location on Location {
  __id
  id
  city
  address
  address_2
  display
  coordinates {
    lat
    lng
  }
  day_schedules {
    start_time
    end_time
    day_of_week
  }
}

fragment GenericGrid_artworks on Artwork {
  __id
  id
  image {
    aspect_ratio
  }
  ...Artwork_artwork
}
