query FairsQuery(
  $count: Int!
  $cursor: String
) {
  me {
    ...Fairs_me_1G22uz
    __id
  }
}

fragment Fairs_me_1G22uz on Me {
  followsAndSaves {
    fairs(first: $count, after: $cursor) {
      edges {
        node {
          profile {
            id
            is_followed
            __id
          }
          exhibition_period
          name
          counts {
            partners
          }
          href
          image {
            url
          }
          start_at
          end_at
          __id
          __typename
        }
        cursor
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
  __id
}
