query FavoriteFairsQuery {
  me {
    ...Fairs_me
    __id
  }
}

fragment Fairs_me on Me {
  followsAndSaves {
    fairs(first: 10, after: "") {
      edges {
        node {
          profile {
            id
            is_followed
            __id
          }
          exhibition_period
          name
          counts {
            partners
          }
          href
          image {
            url
          }
          start_at
          end_at
          __id
          __typename
        }
        cursor
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
  __id
}
