query FavoriteShowsQuery {
  me {
    ...Shows_me
    __id
  }
}

fragment Shows_me on Me {
  followsAndSaves {
    shows(first: 10) {
      pageInfo {
        startCursor
        endCursor
        hasPreviousPage
        hasNextPage
      }
      edges {
        node {
          ...SavedShowItemRow_show
          __id
          __typename
        }
        cursor
      }
    }
  }
  __id
}

fragment SavedShowItemRow_show on Show {
  id
  _id
  __id
  is_followed
  name
  partner {
    __typename
    ... on Partner {
      name
    }
    ... on ExternalPartner {
      name
      __id
    }
    ... on Node {
      __id
    }
  }
  href
  exhibition_period
  status
  cover_image {
    url
    aspect_ratio
  }
  start_at
  end_at
}
