query GlobalMapTestsQuery(
  $citySlug: String!
  $maxInt: Int!
) {
  viewer {
    ...GlobalMap_viewer_3La17j
  }
}

fragment GlobalMap_viewer_3La17j on Viewer {
  city(slug: $citySlug) {
    name
    sponsoredContent {
      introText
      artGuideUrl
    }
    coordinates {
      lat
      lng
    }
    shows(discoverable: true, first: $maxInt, sort: START_AT_ASC) {
      edges {
        node {
          id
          _id
          __id
          name
          status
          href
          is_followed
          exhibition_period
          cover_image {
            url
          }
          location {
            coordinates {
              lat
              lng
            }
            __id
          }
          type
          start_at
          end_at
          partner {
            __typename
            ... on Partner {
              name
              type
            }
            ... on ExternalPartner {
              name
              __id
            }
            ... on Node {
              __id
            }
          }
        }
      }
    }
    fairs(first: $maxInt) {
      edges {
        node {
          id
          name
          exhibition_period
          counts {
            partners
          }
          location {
            coordinates {
              lat
              lng
            }
            __id
          }
          image {
            image_url
            aspect_ratio
            url
          }
          profile {
            icon {
              id
              href
              height
              width
              url(version: "square140")
            }
            __id
            id
            name
          }
          start_at
          end_at
          __id
        }
      }
    }
  }
}
