query ShowsQuery(
  $count: Int!
  $cursor: String
) {
  me {
    ...Shows_me_1G22uz
    __id
  }
}

fragment Shows_me_1G22uz on Me {
  followsAndSaves {
    shows(first: $count, after: $cursor) {
      pageInfo {
        startCursor
        endCursor
        hasPreviousPage
        hasNextPage
      }
      edges {
        node {
          ...SavedShowItemRow_show
          __id
          __typename
        }
        cursor
      }
    }
  }
  __id
}

fragment SavedShowItemRow_show on Show {
  id
  _id
  __id
  is_followed
  name
  partner {
    __typename
    ... on Partner {
      name
    }
    ... on ExternalPartner {
      name
      __id
    }
    ... on Node {
      __id
    }
  }
  href
  exhibition_period
  status
  cover_image {
    url
    aspect_ratio
  }
  start_at
  end_at
}
