query indexTestsFairHeaderQuery {
  fair(id: "sofa-chicago-2018") {
    ...FairHeader_fair
    __id
  }
}

fragment FairHeader_fair on Fair {
  id
  _id
  name
  counts {
    artists
    partners
  }
  followed_content {
    artists {
      name
      href
      id
      _id
      __id
    }
    galleries {
      _id
      name
      __id
    }
  }
  partner_names: shows_connection(first: 2) {
    edges {
      node {
        id
        partner {
          __typename
          ... on Partner {
            profile {
              name
              id
              _id
              __id
            }
          }
          ... on Node {
            __id
          }
          ... on ExternalPartner {
            __id
          }
        }
        __id
      }
    }
  }
  artists_names: artists(first: 2) {
    edges {
      node {
        name
        href
        id
        _id
        __id
      }
    }
  }
  image {
    image_url
    aspect_ratio
    url
  }
  profile {
    icon {
      id
      href
      height
      width
      url(version: "square140")
    }
    __id
    id
    name
    is_followed
  }
  start_at
  end_at
  __id
}
